mapper.select:
select	bno, title, content, writer, views, likes, photo, regdate, updatedate, pettype, user_no
from	pet_board
where	bno=#{bno}

mapper.update:
update	pet_board
set		title=#{title}, content=#{content}, photo=#{photo}, pettype=#{pettype}, updatedate=sysdate
where	bno=#{bno}

mapper.select:
select	user_no
from	pet_board_heart
where	bno=#{bno}

mapper.update:
update	pet_board
set		likes = likes + 1
where	bno = #{bno}

update	pet_board
set		likes = likes - 1
where	bno = #{bno}

update	pet_board
set		views = views + 1
where	bno = #{bno}
